package org.projectemail.emailapp.services;

import com.fasterxml.jackson.databind.ObjectMapper;
import jakarta.mail.MessagingException;
import jakarta.mail.internet.MimeMessage;
import org.projectemail.emailapp.dtos.RequestDto;
import org.springframework.beans.factory.annotation.Autowired;
import org.springframework.mail.MailException;
import org.springframework.mail.javamail.JavaMailSender;
import org.springframework.mail.javamail.MimeMessageHelper;
import org.springframework.stereotype.Service;

@Service
public class EmailService {
    private final JavaMailSender javaMailSender;
    private final EmailTemplateLoader templateLoader;

    @Autowired
    public EmailService(JavaMailSender javaMailSender, EmailTemplateLoader templateLoader) {
        this.javaMailSender = javaMailSender;
        this.templateLoader = templateLoader;
    }

    public boolean sendEmail(RequestDto requestDto) {
        try {
            MimeMessage message = javaMailSender.createMimeMessage();
            MimeMessageHelper helper = new MimeMessageHelper(message, true);
            helper.setTo(requestDto.getEmailReceiver());

            helper.setSubject(requestDto.getEmailSubject());

            String htmlContent = templateLoader.loadTemplate("templates/email_template.html");
            htmlContent = generateHtmlContent(htmlContent, requestDto.getEmailSubject(), requestDto.getEmailBody());
            helper.setText(htmlContent, true);

            javaMailSender.send(message);
            return true;
        } catch (MailException | MessagingException e) {
            e.printStackTrace();
            return false;
        }
    }

    private String generateHtmlContent(String template, String emailSubject, String emailBody) {
        template = template.replace("$SUBJECT_CONTENT$", emailSubject);
        template = template.replace("$BODY_CONTENT$", emailBody);
        template = template.replace("$IMAGE_CONTENT$", ImageContent.headerImage);

        return template;
    }

    private static class ImageContent {
        public static final String headerImage = "data:image/jpeg;base64,";
    }
}